css:
/* ===============================
   TITLE / BRAND
   =============================== */
.print-heading h2,
.print-heading h2 div {
    margin-bottom: 0 !important;
    line-height: 0.5 !important; /* keeps it compact */
}

/* ===============================
   FIELD LABELS (bold + black)
   =============================== */
.print-format .row label,
.print-format .field-label,
.print-format strong,
.print-format b {
    color: #000 !important;
    font-weight: 600 !important;
}

/* ===============================
   FIELD SPACING (compact)
   =============================== */
/* Reduce gap between stacked rows */
.print-format .row {
    margin-top: 0 !important;
    margin-bottom: 0 !important; /* no extra gap */
}

/* Reduce vertical padding inside columns */
.print-format .row [class*="col-"] {
    padding-top: 0 !important;
    padding-bottom: 2px !important; /* slight breathing room */
}

/* Keep paragraphs tidy */
.print-format p {
    margin: 0 0 4px 0 !important;
}

/* Do NOT alter letterhead header/footer */
.print-format .letter-head .row,
.print-format .letter-footer .row {
    margin-bottom: 15px !important;
    padding: inherit !important;
}

/* Reduce vertical spacing for label–value pairs in header rows */
.print-format .row > div {
    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
    line-height: 1 !important; /* tighter lines */
}

/* Optional: make label and value closer */
.print-format .row label {
    margin-bottom: 0 !important;
    line-height: 1 !important;
}

/* ===============================
   ITEMS TABLE – MINIMAL LINES
   =============================== */
.print-format table.table,
.print-format table.table-bordered {
    width: 100% !important;
    border-collapse: collapse !important;
    margin-top: 10px !important;
    font-size: 12px !important;
    line-height: 1.5;
    background: transparent !important;
    border: none !important;
}

/* Header: dark underline */
.print-format table.table thead th {
    background: transparent !important;
    color: #111 !important;
    font-weight: 600 !important;
    padding: 8px 10px !important;
    border: none !important;
    border-bottom: 1px solid #111 !important;
}

/* Body: soft separators only */
.print-format table.table tbody td {
    padding: 8px 10px !important;
    border: none !important;
    border-top: 1px solid #e5e7eb !important;
    background: #fff !important;
    vertical-align: top !important;
}

/* Last body row: no bottom border */
.print-format table.table tbody tr:last-child td {
    border-bottom: none !important;
}

/* Footer/totals rows */
.print-format table.table tfoot td {
    padding: 8px 10px !important;
    border: none !important;
    border-top: 1px solid #111 !important;
    font-weight: 600 !important;
    background: transparent !important;
}

/* ===============================
   PDF COLOR RELIABILITY
   =============================== */
.print-format {
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
}

/* ===============================
   HELPERS FOR NUMERIC ALIGN + NO WRAP
   =============================== */
.print-format .ta-r {
    text-align: right !important;
}

.print-format .nowrap {
    white-space: nowrap !important;
}

/* Optional: keep currency + amount on one line in right-aligned cols */
.print-format td.ta-r,
.print-format th.ta-r {
    white-space: nowrap !important;
}

/* Optional: if you ever drop CSS-based subtext (when not inlining) */
.print-format .item-desc {
    font-size: 11px;
    color: #6b7280 !important;
    margin-top: 2px;
}

/* ===============================
   HEADER CONTAINER
   =============================== */
.print-heading {
    display: flex !important;
    justify-content: space-between !important;
    align-items: flex-start !important;
    margin-bottom: 12px !important;
    border-bottom: 2px solid #222 !important;
    padding-bottom: 6px !important;
}

/* ===============================
   LEFT SIDE: INVOICE TITLE
   =============================== */
.invoice-title {
    line-height: 1.1 !important;
}

.invoice-title h2 {
    margin: 0 !important;
    font-size: 22px !important;
    font-weight: 700 !important;
    color: #111 !important;
    letter-spacing: 0.3px !important;
}

.invoice-title .sub-heading {
    font-size: 12px !important;
    color: #555 !important;
    font-weight: 500 !important;
}

/* ===============================
   RIGHT SIDE: COMPANY NAME
   =============================== */
.company-name {
    text-align: right !important;
    line-height: 1 !important;
}

.company-name h2 {
    margin: 0 !important;
    font-size: 20px !important;
    font-weight: 800 !important;
    color: #f5b301 !important; /* force brand color */
    text-transform: uppercase !important;
    letter-spacing: 1px !important;
}

.company-name h2 span {
    color: #111 !important; /* Second half of the brand name */
}

/* ===============================
   INVISIBLE SPACERS
   =============================== */
.spacer.spacer-1 {
    height: 18px !important;
}

.spacer.spacer-2 {
    height: 8px !important;
}

/* ===============================
   COMPANY BRAND: Code.Solutions
   =============================== */
.company-brand {
    margin: 0 !important;
    font-size: 20px !important;
    font-weight: 800 !important;
    text-transform: uppercase !important;
    letter-spacing: 1px !important;
}

.company-brand span.code {
    color: #4169E1 !important; /* Royal Blue for "Code." */
}

.company-brand span.solutions {
    color: #111 !important; /* Black for "Solutions" */
}

/* ===============================
            BANK DETAILS
   =============================== */

.bank-details {
    font-size: 12px !important;
    line-height: 1.4 !important;
}

.bank-details .field-label {
    font-weight: 600 !important;
    margin-bottom: 4px !important;
}

.bank-details p {
    margin: 0 0 2px 0 !important;
}

/* ===============================
        COMPANY ADDRESS
   =============================== */

.company-address {
    text-align: right !important;
    font-size: 12px !important;
    color: #555 !important;
    font-weight: 500 !important;
    line-height: 1.2 !important;
    margin-top: 2px !important;
}
