css:
/* ===============================
   TITLE / BRAND
   =============================== */
.print-heading h2,
.print-heading h2 div {
  color: #000000 !important;   /* brand yellow */
  font-weight: 700 !important;
  margin-bottom: 0 !important;
  line-height: 0.5 !important;  /* keeps it compact */
}

/* ===============================
   FIELD LABELS (bold + black)
   =============================== */
.print-format .row label,
.print-format .field-label,
.print-format strong,
.print-format b {
  color: #000 !important;
  font-weight: 600 !important;
}

/* ===============================
   FIELD SPACING (compact)
   =============================== */
/* Reduce gap between stacked rows */
.print-format .row {
  margin-top: 0 !important;
  margin-bottom: 0 !important;  /* no extra gap */
}

/* Reduce vertical padding inside columns */
.print-format .row [class*="col-"] {
  padding-top: 0 !important;
  padding-bottom: 2px !important;  /* slight breathing room */
}

/* Keep paragraphs tidy */
.print-format p { margin: 0 0 4px 0 !important; }

/* Do NOT alter letterhead header/footer */
.print-format .letter-head .row,
.print-format .letter-footer .row {
  margin-bottom: 15px !important;
  padding: inherit !important;
}

/* Reduce vertical spacing for label–value pairs in header rows */
.print-format .row > div {
  margin-bottom: 0 !important;
  padding-bottom: 0 !important;
  line-height: 1 !important;   /* tighter lines */
}

/* Optional: make label and value closer */
.print-format .row label {
  margin-bottom: 0 !important;
  line-height: 1 !important;
}


/* ===============================
   ITEMS TABLE – MINIMAL LINES
   =============================== */
.print-format table.table,
.print-format table.table-bordered {
  width: 100% !important;
  border-collapse: collapse !important;
  margin-top: 10px !important;
  font-size: 12px !important;
  line-height: 1.5;
  background: transparent !important;
  border: none !important;
}

/* Header: dark underline */
.print-format table.table thead th {
  background: transparent !important;
  color: #111 !important;
  font-weight: 600 !important;
  padding: 8px 10px !important;
  border: none !important;
  border-bottom: 1px solid #111 !important;
}

/* Body: soft separators only */
.print-format table.table tbody td {
  padding: 8px 10px !important;
  border: none !important;
  border-top: 1px solid #e5e7eb !important;
  background: #fff !important;
  vertical-align: top !important;
}

/* Last body row: no bottom border */
.print-format table.table tbody tr:last-child td {
  border-bottom: none !important;
}

/* Footer/totals rows */
.print-format table.table tfoot td {
  padding: 8px 10px !important;
  border: none !important;
  border-top: 1px solid #111 !important;
  font-weight: 600 !important;
  background: transparent !important;
}

/* ===============================
   PDF COLOR RELIABILITY
   =============================== */
.print-format { -webkit-print-color-adjust: exact; print-color-adjust: exact; }

/* ===============================
   HELPERS FOR NUMERIC ALIGN + NO WRAP
   =============================== */
.print-format .ta-r { text-align: right !important; }
.print-format .nowrap { white-space: nowrap !important; }

/* Optional: keep currency + amount on one line in right-aligned cols */
.print-format td.ta-r,
.print-format th.ta-r { white-space: nowrap !important; }

/* Optional: if you ever drop CSS-based subtext (when not inlining) */
.print-format .item-desc { font-size: 11px; color: #6b7280 !important; margin-top: 2px; }

